INSERT INTO `index_booklist` VALUES ('8', '&#x7EEE;&#x793C;&#x6851;&#x6709;&#x8BDD;&#x8981;&#x8BB2;', '称赞善良的珍贵，歌颂神圣的美丽...正因为这样的真理深信不疑，我才会浪费了近二十年的人生...我根本没有察觉，自己的本性与意念完全与这样的真理背道而驰...正义要有与之对立的邪恶才能存在...最崇高的理想与最丑恶的愿望，有着相同的意义...这世界上已经拥有了一位正义的伙伴...所以...男人愉悦一点又有什么错？', 'https://novel-cdn.kuangxiangit.com/uploads/allimg/c180226/26-02-18161027-29257.jpg', '\n                                    <span>41.8&#x4E07;</span>&#xFE31;\n                                    <span>&#x52A8;&#x6F2B;&#x7A7F;&#x8D8A;</span>\n                                ', 'http://www.hbooker.com/book/100061595', '宅文推荐', 'https://avatar.kuangxiangit.com/novel/img_item/img-2016-09/496255/avatar/thumb_a1a0ecac390d8860effc65aa69920c28.jpg', '\n                                            【离恨纷飞】', '2358\n                                            \n                                        ', null);
