INSERT INTO `index_booklist` VALUES ('10', '&#x4E00;&#x62F3;&#x8D85;&#x4EBA;&#x5728;&#x7F8E;&#x597D;&#x4E16;&#x754C;', '埼玉穿越到美好世界，随身一只怪人萝莉……什么？还能再种出一只？——这是一个埼玉老师穿越到美（dou)好(bi)世界的故事。【本文是以《一拳超人》和《为美好的世界献上祝福》为基础创作的二次同人，在此基础上会有部分原创设定，请勿太过较真。】', 'https://novel-cdn.kuangxiangit.com/uploads/allimg/c180321/21-03-18182032-93895-100063739.jpg', '\n                                    <span>28.0&#x4E07;</span>&#xFE31;\n                                    <span>&#x52A8;&#x6F2B;&#x7A7F;&#x8D8A;</span>\n                                ', 'http://www.hbooker.com/book/100063739', '宅文推荐', 'https://avatar.kuangxiangit.com/novel/img-2018-03/3119292/avatar/thumb_a08854119673e61bb97f220a545bbf30.jpg', '\n                                            【懒喵一世】', '1527\n                                            \n                                        ', null);
